import { useState } from 'react';
import { Stage, Layer, Rect } from 'react-konva';

function Main() {
    var p = {
        fill: "white"
    }

    const [tab, setTab] = useState(Array.from({length: 5},()=> Array.from({length: 5}, () => Object.create(p))));

    const colorSelection = (actual) => {

    }

    const RectOnClick = (e) => {
        let newTab = [...tab]
        newTab[e.i][e.n].fill = (e.fill === "white" ? "black" : "white")        
        setTab(newTab)
    }

    return (
      <div>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>
            {
                tab.map((row, i) => (
                    row.map((column, n) => (
                        <Rect
                        i={i}
                        n={n}
                        x={10 + i * 100}
                        y={10 + n * 100}
                        width={100}
                        height={100}
                        fill={column.fill}
                        shadowBlur={10}
                        onClick={e => RectOnClick(e.target.attrs)}
                        />
                    ))
                ))
            }
          </Layer>
        </Stage>
      </div>
    );
  }

export default Main;
