import React, { useState } from 'react';
import { Stage, Layer, Rect, Text } from 'react-konva';

function Main() {
    const mapSize = 8
    const p = {
        type: 0 // default rect type
    }

    const [tab, setTab] = useState(Array.from({length: mapSize},()=> Array.from({length: mapSize}, () => Object.create(p))));

    // Rect type
    const RectType = [
        {type: 0, color: 'white', name: 'empty'},
        {type: 1, color: 'black', name: 'black'},
        {type: 2, color: 'green', name: 'start'},
        {type: 3, color: 'red', name: 'end'},
        {type: 4, color: 'grey', name: 'checked '},
    ]    

    const nextRectType = (actual) => {
        return (actual + 1) % 4
    }

    const getColorFromType = (type) => {
        return RectType[type].color
    }

    const RectOnClick = (e) => {
        let newTab = [...tab]
        newTab[e.i][e.n].type = nextRectType(tab[e.i][e.n].type)
        setTab(newTab)
    }

    const isRectExist = (i, n) => {
        if (0 > i || i > mapSize - 1)
            return false
        if (0 > n || n > mapSize - 1)
            return false
        return true
    }

    const checkPrevMoves = (i, n, prevMoves) => {
        for (let index = 0; index < prevMoves.length; index++)
            if (prevMoves[index] === [i, n])
                return false
        return true
    }

    const updatePrevMoves = (i, n, prevMoves) => {
        if (prevMoves.length >= 4) {
            prevMoves.pop()
            prevMoves.push([i, n])
            return
        }
        prevMoves.push([i, n])
    }

    const engine = (i, n, prevMoves) => {
        if (isRectExist(i, n) === false)
            return false
        console.log(prevMoves)
        updatePrevMoves(i, n, prevMoves)
        let newTab = [...tab]
        newTab[i][n].type = 4
        setTab(newTab)
        console.log('i = ' + i)
        console.log('n = ' + n)
        setTimeout(() => { console.log("ok ok"); }, 2000);

        if (checkPrevMoves(i + 1, n) === true)
            return engine(i + 1, n, [...prevMoves]);
        if (checkPrevMoves(i - 1, n) === true)
            return engine(i - 1, n, [...prevMoves]);
        if (checkPrevMoves(i, n + 1) === true)
            return engine(i, n + 1, [...prevMoves]);
        if (checkPrevMoves(i, n - 1) === true)
            return engine(i, n - 1, [...prevMoves]);
    }

    return (
      <div>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>
            <Text
            x={0}
            y={0}
            text='RUN'
            fontSize={100}
            onClick={() => engine(0, 0, [[0, 0]])}
            />
            {
                tab.map((row, i) => (
                    row.map((column, n) => (
                        <Rect
                        i={i}
                        n={n}
                        x={10 + i * 100}
                        y={100 + n * 100}
                        width={100}
                        height={100}
                        fill={getColorFromType(column.type)}
                        shadowBlur={10}
                        onClick={e => RectOnClick(e.target.attrs)}
                        />
                    ))
                ))
            }
          </Layer>
        </Stage>
      </div>
    );
  }

export default Main;
