import { useState } from 'react';
import { Stage, Layer, Rect } from 'react-konva';

function Main() {
    const [tab, setTab] = useState(Array.from({length: 5},()=> Array.from({length: 5}, () => null)));

    const ReactOnClick = () => {
        console.log('ok')
        this.fill = "blue"
    }

    return (
      <div>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>
            {
                tab.map((row, i) => (
                    row.map((column, n) => (
                        <Rect
                        x={10 + i * 100}
                        y={10 + n * 100}
                        width={100}
                        height={100}
                        fill="white"
                        shadowBlur={10}
                        onClick={e => (this.fill("blue"))}
                        />
                    ))
                ))
            }
          </Layer>
        </Stage>
      </div>
    );
  }

export default Main;
