import { useState } from 'react';
import { Stage, Layer, Rect } from 'react-konva';

function Main() {
    var p = {
        color: "white"
    }

    const [tab, setTab] = useState(Array.from({length: 5},()=> Array.from({length: 5}, () => p)));

    const ReactOnClick = (e) => {
        let newTab = [...tab]
        newTab[e.i][e.n] = "blue"        
        setTab(newTab)
        console.log(e)
    }

    return (
      <div>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>
            {
                tab.map((row, i) => (
                    row.map((column, n) => (
                        <Rect
                        i={i}
                        n={n}
                        x={10 + i * 100}
                        y={10 + n * 100}
                        width={100}
                        height={100}
                        fill={column.color}
                        shadowBlur={10}
                        onClick={e => ReactOnClick(e.target.attrs)}
                        />
                    ))
                ))
            }
          </Layer>
        </Stage>
      </div>
    );
  }

export default Main;
